var http = require("https");

var options = {
  "method": "X-RESTLET",
  "hostname": "api.directfreight.com",
  "port": null,
  "path": "/v1/alerts/%7Bboard_type%7D",
  "headers": {
    "Accept": "application/json",
    "Content-Type": "application/json",
    "api-token": "REPLACE_KEY_VALUE"
