  ("Content-Type", "application/json");
  ("api-token", "REPLACE_KEY_VALUE");
] in

Client.call ~headers (Code.method_of_string "X-RESTLET") uri
>>= fun (res, body_stream) ->
  (* Do stuff with the result *)

